mapper.select:
SELECT 

FROM hmac_client a

WHERE a.id = #{id}

SELECT 

FROM hmac_client a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.clientType = #{clientType}


		AND a.client_name = #{clientName}


		AND a.client_id = #{clientId}


		AND a.client_secret = #{clientSecret}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM hmac_client a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
